INSERT INTO `anime` VALUES (91, 'TV', 'Granbelm', 'In a world that long ago featured the existence of magic, but has long since lost that ability. The story begins when the very normal high school student Mangetsu Kohinata meets Shingetsu Ernesta Fukami, who has migrated back to Japan from Germany, on a night with a full moon.\n\n(Source: ANN)\n\nEpisode 1 and 2 will be previewed at a screening at United Cinemas Toyosu on June 22, 2019. Regular broadcasting will begin in July.', '2019-06-21', NULL, 'Unknown', '99942.jpg', 'Currently Airing', 'Unknown', 'https://www.youtube.com/embed/VqW-YbHugT0?enablejsapi=1&wmode=opaque&autoplay=1', 'Original', 'Summer 2019');
INSERT INTO `anime` VALUES (92, 'Movie', 'K: Seven Stories Movie 4 - Lost Small World - Ori no Mukou ni', 'Misaki Yata is a youth whose reckless and immature attitude has led him to be shunned by his classmates. Saruhiko Fushimi is a loner who views the world in a pessimistic way. Despite being polar opposites, the two become the best of friends.\n\n(Source: TubiTV)', '2018-10-05', NULL, '57 min.', '91852.jpg', 'Finished Airing', '1', 'https://www.youtube.com/embed/jafdker57zE?enablejsapi=1&wmode=opaque&autoplay=1', 'Light novel', NULL);
