INSERT INTO `anime` VALUES (136, 'Special', 'Dragon Ball: Ossu! Kaettekita Son Gokuu to Nakama-tachi!!', 'Based on an original concept by the original author Akira Toriyama, the story, set shortly after the defeat of Majin Buu, pits Son Gokuu and his friends against a new, powerful enemy.\n\nThis special introduces four new characters: Abo and Kado red and blue aliens wearing battle fatigues similar to Frieza\'s army, one tiny yellow alien, and a young Saiyan child who vaguely resembles Vegeta named Tarble. Though very little about the plot has been revealed, Tarble has arrived on Earth, being pursued by Abo and Kado.\n\n(Source: Wikipedia)', '2008-09-20', NULL, '34 min.', '82828.jpg', 'Finished Airing', '1', NULL, 'Manga', NULL);
INSERT INTO `anime` VALUES (137, 'TV', 'Fushigi Mahou Fun Fun Pharmacy', 'In a small town, there is a small pharmacy called Fun Fun Parmacy. Popuri, a girl who recently moved in downtown, discovers this pharmacy and meets Miss Fukiko, who is actually a witch. Miss Fukiko gives Popuri a mysterious bottle that can create magic spirits.', '1998-02-14', '1999-02-05', '12 min. per ep.', '49799.jpg', 'Finished Airing', '48', NULL, 'Unknown', 'Winter 1998');
