INSERT INTO `anime` VALUES (308, 'Music', '6â¥Princess', '\"6HP\" is a magical girl animation produced by Murakami\'s Ponkotan studio and features character designs by mebae. \"6HP Six Hearts Princess Pink or Black\" is a promotional video for the series cosmetics, created by Shu Uemura. Music is by livetune and vocal is by Hatsune Miku.', '2013-10-03', NULL, '3 min.', '55347.jpg', 'Finished Airing', '1', NULL, 'Unknown', NULL);
