INSERT INTO `anime` VALUES (311, 'Movie', 'Pororo Geugjangpan Super Sseolmae Daemoheom', 'Pororo and the gang entry a sleigh racing contest after getting guidance under a \'champion\'. Turns out the champion was a liar and now the gang has to fight tooth and nail to win first place in order to keep their pride after talking big.', '2013-01-22', NULL, '1 hr. 16 min.', '66629.jpg', 'Finished Airing', '1', NULL, 'Unknown', NULL);
