INSERT INTO `anime` VALUES (314, 'OVA', 'Hyakujitsu no Bara', 'Two soldiers from warring countries are bound by a pledge as master and servant. Taki Reizen is a Commander of sublime beauty, shouldering the fate of his nation. Called \"Mad Dog\" because of his rough temperament, Klaus has sworn his loyalty to him as a knight. Despite this, those around them are cold and disapproving, full of various misgivings. For all their genuine feelings, what will come of love made cruel by the violence of war? \n', '2009-05-28', '2009-10-22', '24 min. per ep.', '34517.jpg', 'Finished Airing', '2', NULL, 'Manga', NULL);
INSERT INTO `anime` VALUES (315, 'Music', 'Jijii: Owari no Hajimari', 'A music video for Cosmos People\'s song \"Jijii: Owari no Hajimari\".', '2014-04-17', NULL, '5 min.', '78643.jpg', 'Finished Airing', '1', NULL, 'Music', NULL);
