INSERT INTO `anime` VALUES (334, 'OVA', 'Ano Yama ni Noborou yo', 'Three friends find themselves on an adventure to Rainbow Mountain, a magical place where the first rainbow is said to have been created. However, getting to the top of the mountain requires courage and perseverance in the face of many challenges.', '2003-01-01', NULL, '30 min.', '50217.jpg', 'Finished Airing', '1', NULL, 'Unknown', NULL);
