INSERT INTO `anime` VALUES (403, 'OVA', 'Kenritsu Umisora Koukou Yakyuubu-in Yamashita Tarou-kun', 'Based on the popular 80\'s baseball manga by Koseki Kouji.\n\nTarou Yamashita loves baseball, but he was always the worst player. However, through effort and dedication he begins to shine in the Umisora High team.\n\n(Source: AniDB)', '1988-09-22', NULL, '34 min.', '67439.jpg', 'Finished Airing', '1', NULL, 'Unknown', NULL);
