INSERT INTO `anime` VALUES (405, 'TV', 'Kemono Michi', 'Professional wrestler and animal lover Shibata Genzo is suddenly summoned to another world. Greeted by a princess, she requests to get rid of the evil beasts roaming in this world. Outraged that he was asked to kill monsters, Genzo German suplexes the princess. Unable back to get back to Earth, he decides to live a peaceful life and manage a \"monster\" pet shop. \n\n(Source: MU, edited)', '2019-10-01', NULL, 'Unknown', '98330.jpg', 'Not yet aired', 'Unknown', NULL, 'Manga', 'Fall 2019');
INSERT INTO `anime` VALUES (406, 'Special', 'Girls & Panzer: Fushou Akiyama Yukari no Sensha Kouza - Extra', 'In honor of Girls & Panzer TV & OVA receiving a 5.1ch Blu-ray Disc BOX set, Bandai Namco released a new Akiyama Yukari\'s Tank Course \"Extra\" on its official YouTube channel.\n\nA non-watermarked version of the video will be included on the BD Box set as well. It is slated to be released December 21, 2018.', '2018-12-20', NULL, '26 min.', '95121.jpg', 'Finished Airing', '1', NULL, 'Original', NULL);
