INSERT INTO `anime` VALUES (410, 'Movie', 'Dragon Ball Z Movie 10: Kiken na Futari! Super Senshi wa Nemurenai', 'After his loss to Goku, Broly crash lands and hibernates on earth. After some time, he is awakened by Trunks and Goten, who Broly believes is Kakarott, and goes on a rampage to kill both of them. At the same time, Gohan is on his way to challenge the Legendary Super Saiyan alone.', '1994-03-11', NULL, '50 min.', '93673.jpg', 'Finished Airing', '1', 'https://www.youtube.com/embed/vib86sMACCc?enablejsapi=1&wmode=opaque&autoplay=1', 'Manga', NULL);
