INSERT INTO `anime` VALUES (426, 'Movie', 'Precure All Stars Movie DX: Minna TomodachiâKiseki no Zenin Daishuugou!', 'Love, Miki and Inori are on their way to a dance contest, but get lost on their way to Minato Mirai. Still looking for their way, they are attacked by a monster. When the three Fresh Precure girls start to fight it, other Precures show up to support them. They all have to combine their powers to win against this mighty enemy.', '2009-03-19', NULL, '1 hr. 10 min.', '20243.jpg', 'Finished Airing', '1', NULL, 'Original', NULL);
