INSERT INTO `anime` VALUES (615, 'Special', 'iDOLM@STER Xenoglossia Specials', 'Specials bundled with the iDOLM@STER Xenoglossia DVD releases.\n\nEpisodes\n#1: Izayoi-ryou e Youkoso!\n#2: Nantettatte Idol!\n#3: Komugiiro no Mermaid\n#4: Watashi ga Obasan ni Natte mo\n#5: Haru nanoni\n#6: Sekaijuu no Dare yori Kitto\n#7: SWEET MEMORIES\n#8: Itoshisa to Setsunasa to Kokoro Tsuyosa to\n#9: Episode 26 epilogue', '2007-07-26', '2008-03-24', '5 min. per ep.', '24320.jpg', 'Finished Airing', '9', NULL, 'Unknown', NULL);
INSERT INTO `anime` VALUES (616, 'OVA', 'Shamanic Princess', 'From the Guardian World, home of mages, Tiara has been sent on a mission: recover the stolen Throne of Yord, the most powerful magic artifact of all. But Tiara finds herself in a dilemmaâthe Throne has been taken by her former lover, and there is more to the situation than meets the eye... \n\n(Source: ANN)', '1996-06-24', '1998-06-24', '30 min. per ep.', '94311.jpg', 'Finished Airing', '6', NULL, 'Original', NULL);
