INSERT INTO `anime` VALUES (619, 'Movie', 'High & Low: G-Sword (Movie)', 'High & Low: G-Sword manner movies that aired in theaters before main features in part promote the live-action \"HiGH&LOW The Movie 2: End of Sky\" film that opened August 19, 2017. Each episode stars a different G-Sword gang member. In the shorts, the characters are at a theater in the Sword district and they interact with one another and the camera-headed man known as Movie Thief (Eiga Dorobou), a stand-in character used to represent piracy. The commercials will rotate out at the end of each week.\n\n(Source: ANN)', '2017-08-13', '2017-11-10', '1 min. per ep.', '98332.jpg', 'Finished Airing', '5', NULL, 'Other', NULL);
INSERT INTO `anime` VALUES (620, 'Special', 'SpaceâDandy 2nd Season Picture Drama', 'Picture drama episodes included on the Blu-ray/DVD volume eight, nine, and ten.', '2014-11-20', '2015-01-27', '6 min. per ep.', '72373.jpg', 'Finished Airing', '3', NULL, 'Original', NULL);
INSERT INTO `anime` VALUES (621, 'Movie', 'Juuippiki no Neko to Ahoudori', 'Eleven naughty cats on the run from the neighborhood police chief hear that a giant fish is nearby for the taking. They go on a hazardous quest find it, learning that they must work as a team to reach their goal.\n\n(Source: The Anime Encyclopedia)', '1986-08-26', NULL, '1 hr. 30 min.', '13275.jpg', 'Finished Airing', '1', NULL, 'Picture book', NULL);
INSERT INTO `anime` VALUES (622, 'OVA', 'D-1 Devastator', 'To follow in his missing friend Masato\'s footsteps, Ryo joined the vehicle company Yaesu. Ryo became involved with the Makuhari test course that Masato was also a part of. The leader, Gray Gear, told Ryo, \"If you want to know about Masato, come to the test course late at night and ride the new machine.\"\n\nWhen Ryo got in the new machine, the Vector Versus, and rode it around the course, he was able to exceed 300 kilometers per hour and suddenly warped into a mysterious space!', '1992-12-10', '1993-02-25', '50 min. per ep.', '55845.jpg', 'Finished Airing', '2', NULL, 'Unknown', NULL);
INSERT INTO `anime` VALUES (623, 'OVA', '100%', 'A pretty 22-year-old girl leaves university and gets her dream job as a TV newscaster, only to discover that life is tough at the top. Adaptation of the \"Young Action\" manga by Michio Yanagisawa.\n\n(Source: AniDB)', '1990-09-06', NULL, '53 min.', '17650.jpg', 'Finished Airing', '1', NULL, 'Unknown', NULL);
INSERT INTO `anime` VALUES (624, 'TV', 'Kaitou Pride', 'Each episode features a part of the story in which a detective tries to hunt down and capture a notorious and narcissistic thief. This series aired daily, Monday to Friday, in 8 minutes segments to complete a single story by the end of the week.\n\n(Source: AniDB)', '1965-05-30', '1965-11-03', '8 min. per ep.', '53297.jpg', 'Finished Airing', '105', NULL, 'Unknown', 'Spring 1965');
