INSERT INTO `anime` VALUES (632, 'Music', 'Supa Dupa Fly feat. Shounan no Kaze, Moomin, Kenty Gross, Bes, Apollo, Natural Weapon, Doraku', 'Official music video for Han-kun\'s song Supa Dupa Fly featuring Shounan no Kaze, Moomin, Kenty Gross, Bes, Apollo, Natural Weapon, Doraku in a massive ballad. It celebrates Han-kun\'s 10th anniversary as a hip-hop artist and the single will be available for sale the following day. The song\'s title is a homage to American rapper Missy Elliott\'s debut album of the same name from 1997.', '2018-10-08', NULL, '3 min.', '95447.jpg', 'Finished Airing', '1', NULL, 'Original', NULL);
