INSERT INTO `anime` VALUES (641, 'TV', 'Fuuka', 'Yuu Haruna just moved into town and loves to use Twitter. Out on his way to buy dinner, he bumps into a mysterious girl, Fuuka Akitsuki, who breaks his phone thinking he was trying to take a picture of her panties. How will his new life change now?\n\n(Source: MAL News)', '2017-01-05', '2017-03-23', '24 min. per ep.', '83735.jpg', 'Finished Airing', '12', 'https://www.youtube.com/embed/NNv22mAw8KE?enablejsapi=1&wmode=opaque&autoplay=1', 'Manga', 'Winter 2017');
