INSERT INTO `anime` VALUES (646, 'OVA', 'Stratos 4 Advance Kanketsu-hen', 'Crisis befalls the main characters and the Earth as well! How do they deal with the conspiracies that they have uncovered, while protecting the Earth too? (And what happens to the cliffhanger at the end of the previous episode?)\n\nIn these final two episodes of Stratos 4 Advance are the exciting conclusion to the whole Stratos 4 series. The main characters will have to draw on all the resources they have, their friends and allies and their own sheer force of will, to \"bring tomorrow to Earth,\" as one of the characters puts it.', '2006-09-21', '2006-10-21', '27 min. per ep.', '59213.jpg', 'Finished Airing', '2', NULL, 'Unknown', NULL);
