INSERT INTO `anime` VALUES (648, 'ONA', 'Shinkansen Henkei Robo Shinkalion', 'ShoPro\'s new transforming train toy line comes with animated promotional videos on the official YouTube page. Featuring shinkansen which transform into Shinkalions who fight a mysterious shakouki-doguu monster. ', '2015-03-15', NULL, '1 min.', '84483.jpg', 'Currently Airing', 'Unknown', NULL, 'Other', NULL);
