INSERT INTO `anime` VALUES (872, 'Special', 'Tiger & Bunny: Too Many Cooks Spoil the Broth.', 'The official website for the Tiger & Bunny anime announced on Friday that the Pacifico Yokohama\'s National Convention Hall of Yokohama is hosting special Tiger & Bunny concerts with a new short anime on September 19 and 20. The franchise\'s composer Yoshihiro Ike is personally conducting the \"Sternbild Orchestra & Band\" for live performances of music from the television series and two films.\n\n(Source: ANN)', '2015-09-18', NULL, '17 min.', '74269.jpg', 'Finished Airing', '1', NULL, 'Original', NULL);
