INSERT INTO `anime` VALUES (899, 'TV', 'Takunomi.', '20-year-old Michiru Amatsuki moved to Tokyo due to a change of career. She decided to live in a woman-only share house Stella House Haruno with people of different age and occupations. It\'s always fun when there\'s delicious alcohol and meal!!\n\n(Source: TBS Global Business)', '2018-01-11', '2018-03-29', '12 min. per ep.', '95731.jpg', 'Finished Airing', '12', 'https://www.youtube.com/embed/WcJwO3Gq-hs?enablejsapi=1&wmode=opaque&autoplay=1', 'Web manga', 'Winter 2018');
INSERT INTO `anime` VALUES (900, 'ONA', 'Mahou Shoujo? NariaâGirls Nama de Anime wo Tsukuru Sama', 'Urara, Hanabi, and Inaho are three girls who aim to become nationally-renowned characters by creating animation of themselves! They chose the magical girl theme to catch the attention of everyone from children to adults! Can they safely become popular characters in a one-cours magical girl anime?\n\n(Source: MAL News)', '2016-06-30', '2016-09-15', '40 min. per ep.', '81105.jpg', 'Finished Airing', '12', NULL, 'Original', NULL);
